import React, { useState, useEffect } from 'react'
import { NavLink } from 'react-router-dom'



const ProfileContainer = () => {

    const [userInfo, setUser] = useState([])
    const [wallets, setWallets] = useState([])


    const getUserInfo = () => {
        fetch('http://localhost:3000/users', {
          method: "GET",
          headers: {
            Authorization: `Bearer ${localStorage.token}`
          }
        })
        .then(resp => resp.json())
        .then(users => {
            // console.log(users)
            setUser(users)
        })
        };

        useEffect(() => {
            getUserInfo() 
            getWalletInfo()
        }, 
        [])

        const user = userInfo ? userInfo.find(user => user.id == localStorage.userId) : null
        // console.log(user)

        const getWalletInfo = () => {
            fetch('http://localhost:3000/wallet/', {
          method: "GET",
          headers: {
            Authorization: `Bearer ${localStorage.token}`
          }
        })
        .then(resp => resp.json())
        .then(wallets => {
            console.log(wallets)
            setWallets(wallets)
        })
        }

        useEffect(() => getWalletInfo(), [])

    return(
        <div>
            <h4>{user ? user.firstname : null} {user ? user.lastname : null}</h4>
            <p>{user ? user.email : null}</p>
            <p>{user ? user.username : null}</p>


        </div>
    )
}

export default ProfileContainer;
