import React, {useState} from 'react';
import { render } from 'react-dom';
import {withRouter} from 'react-router-dom';


const Login = (props)=> {

    const [loginInfo, setLogin] = useState({})


    const nextPath = (path) => {
        props.history.push(path);
        // console.log(props)
    }

    handleChange = (e) =>{
        setLogin({...loginInfo, [e.target.name]: e.target.value})
    }

    handleSubmit = (e) => {
        e.preventDefault()

        fetch()
    }

    return(
        <div>
            <h2>Login</h2>
            <form onSubmit={(e)=>{handleSubmit(e)}}>
                <label>User Name</label>
                <input onChange={(e) => handleChange(e)} name="username" type="text" />
                <br/>
                <label>Password</label>
                <input onChange={(e) => handleChange(e)} name="password" type="password" />
                <br/>
                <input type="submit" value="Login" />
            </form>
            <button onClick={()=> nextPath('/signup')}>Sign Up</button>
        </div>
    )
}

export default Login;
